<?php!defined('FRAMEWORK_PATH') && exit('FRAMEWORK_PATH not defined.');// 该文件会被 include 执行。if($this->conf['db']['type'] != 'mongodb') {	$db = $this->user->db;	// 与 user model 同一台 db	$tablepre = $db->tablepre;					$db->query("CREATE TABLE IF NOT EXISTS {$tablepre}invitationcode (																	  c_id int(11) NOT NULL auto_increment COMMENT '//id',																	  c_code varchar(50) NOT NULL default '0' COMMENT '//邀请码',																	  c_isValid tinyint(1) NOT NULL default '1' COMMENT '//是否有效',																	  c_date int(11) NOT NULL COMMENT '//更新日期',																	  PRIMARY KEY  (c_id)	);");}?>